import osimport waveimport matplotlib.pyplot as pltfrom scipy.io import wavfiledef create_waveform(directory, output_directory):    if not os.path.exists(output_directory):        os.makedirs(output_directory)    # Проходим по всем файлам в директории    for filename in os.listdir(directory):        if filename.endswith(".wav"):            # Чтение файла            filepath = os.path.join(directory, filename)            samplerate, data = wavfile.read(filepath)            # Создание осциллограммы            plt.figure(figsize=(10, 4))            plt.plot(data)            plt.title("Waveform of " + filename)            plt.ylabel("Amplitude")            plt.xlabel("Samples")            plt.xlim([0, len(data)])            plt.ylim([-2000, 2000])  # предполагается 16-битное аудио            plt.grid(True)            # Сохранение осциллограммы в заданной директории            output_filepath = os.path.join(output_directory, os.path.splitext(filename)[0] + '_waveform.png')            plt.savefig(output_filepath)            plt.close()  # Закрытие фигуры после сохранения, чтобы избежать потребления памяти# Использование функцииcreate_waveform('/Users/aleksejzubel/Documents/work/преобразование данных гироскопа от колонок в аудиофайл /honor50_gyro_samples_speakers/3',                 '/Users/aleksejzubel/Documents/work/преобразование данных гироскопа от колонок в аудиофайл /honor50_gyro_samples_speakers_waveform/3')